mapper.select:
select 

,

from section
where BOOK_ID = #{bookId,jdbcType=VARCHAR}
  and CHAPTER_ID = #{chapterId,jdbcType=VARCHAR}
  and SECTION_ID = #{sectionId,jdbcType=VARCHAR}

mapper.delete:
delete from section
where BOOK_ID = #{bookId,jdbcType=VARCHAR}
  and CHAPTER_ID = #{chapterId,jdbcType=VARCHAR}
  and SECTION_ID = #{sectionId,jdbcType=VARCHAR}
